import { useState } from "react";
import igLogo from "../assets/igLogo.png";
import "./AddRestaurant.css";

const AddRestaurant = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [inputUrl, setInputUrl] = useState('');
  return (
    <div className="container">
      <button className="autofill-button" onClick={() => setIsModalOpen(true)}>
        Autofill with <img src={igLogo} alt="Instagram" className="icon" />
      </button>
      {isModalOpen && (
        <div className="modal-overlay" onClick={() => setIsModalOpen(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <p>
              This is some normal text inside the modal. You can use this space
              to display information or instructions.
            </p>
            <input
              type="text"
              value={inputUrl}
              onChange={(e) => setInputUrl(e.target.value)}
              className="modal-input"
              placeholder="Enter restaurant's profile link url on Instagram"
            />
            <button className="autofill-button" onClick={() => setIsModalOpen(true)}>Confirm</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddRestaurant;
